mapper.select:
SELECT distinct f.id as formId,f.formName name,f.cover,u.nickName creator, f.issystem as isSystem, ((SELECT count(fc.id) FROM form_collect fc where fc.formId=f.id)+f.likenum)as hot from form f left JOIN `user` u on f.userId=u.id left join form_label fl on f.id=fl.formId left join syslabel sl on sl.id=fl.labelId WHERE f.online=1
and sl.labelName like #{labelStr}
order by hot desc limit #{offSet},#{pageSize}

select count(*) from ( SELECT f.id from form f left join form_label fl on f.id=fl.formId left join syslabel sl on fl.labelId=sl.id WHERE f.online=1
and sl.labelName like #{labelStr}
group by f.id) t
